    public boolean checkZip(View v) {
        textInputEditText = (TextInputEditText)v;
        String zip = textInputEditText.getText().toString();
        zip = zip.trim();

        if (zip == null
            || zip.equals("")) {
            textInputEditText.requestFocus();
            textInputEditText.setError("Zip Code cannot be blank");
            return false;
        }

        regex = "[0-9]{1,5}";
        checkRegex = Pattern.compile(regex);
        regexMatcher = checkRegex.matcher(zip);
        if (!regexMatcher.matches()) {
            textInputEditText.requestFocus();
            textInputEditText.setError("Invalid Zip Code");
            return false;
        }

        return true;
    }

    public boolean checkPhone(View v) {

        textInputEditText = (TextInputEditText)v;
        String phone = textInputEditText.getText().toString();
        phone = phone.trim();

        if (phone == null
            || phone.equals("")) {
            textInputEditText.requestFocus();
            textInputEditText.setError("Phone Number cannot be blank");
            return false;
        }

        regex =  "\\d{10}|(?:\\d{3}-){2}\\d{4}|\\(\\d{3}\\)\\d{3}-?\\d{4}";
        checkRegex = Pattern.compile(regex);
        regexMatcher = checkRegex.matcher(phone);
        if (!regexMatcher.matches()) {
            textInputEditText.requestFocus();
            textInputEditText.setError("Invalid Phone Number");
            return false;
        }

        return true;
    }

